parameters.jsCode:
/**
 * n8n Code node: ASS subtitles (single-token, exact onsets, 10ms quantized)
 * - Supports ElevenLabs Scribe v1 (words with {text,start,end,type})
 * - Supports Whisper verbose_json (words with {word,start,end})
 * - Ignores spacing/audio_event tokens
 * - Repairs zero-duration and same-start clusters so nothing gets skipped
 * - No template literals in emit (avoids unterminated template errors)
 */

//////////////////// CONFIG ////////////////////
const CONFIG = {
  wordsToShow: 1,

  // Styling
  fontName: 'Arial',
  fontSize: 90,
  fontColor: 'FFFFFF',
  outlineColor: '000000',
  backgroundColor: '000000',

  highlightFontSize: 90,
  highlightColor: '00FFFF',

  bold: 10,
  italic: 0,
  underline: 0,
  strikeout: 0,
  scaleX: 100,
  scaleY: 100,
  spacing: 0,
  angle: 0,

  borderStyle: 1,
  outline: 5,
  shadow: 2,

  alignment: 8,   // top-center
  marginL: 10,
  marginR: 10,
  marginV: 1270,

  // Resolution fallback
  videoWidth: null,
  videoHeight: null,

  // Timing
  minWordDurSec: 0.08,   // 80 ms helps zero-duration tokens
  minGapCs: 1,           // target 10 ms gap
  globalOffsetMs: 0,     // Scribe tends to be very tight; start with 0ms

  // When dense, allow nudging next start forward up to X cs (1 cs = 10 ms)
  maxStartShiftCs: 5,    // 50 ms max shift to preserve onsets

  // Numeric handling (optional)
  combineNumbers: true,
  attachNumMaxGapSec: 0.30,

  // Sanitization
  dropEmpty: true,              // drop empty-text tokens
  dropPunctOnly: true,          // drop tiny punctuation-only tokens
  tinyDurThresholdSec: 0.03,    // treat <30ms punctuation as noise

  outputFileName: 'sentence_aware_subtitles.ass',
};

//////////////////// HELPERS ////////////////////
function firstDefined(){ for (let i=0;i<arguments.length;i++){ if (arguments[i]!=null) return arguments[i]; } }
function toCsStart(sec, offsetMs){ const s=(Number(sec)||0)+(offsetMs/1000); return Math.max(0, Math.floor(s*100)); }
function toCsEnd(sec, offsetMs){ const s=(Number(sec)||0)+(offsetMs/1000); return Math.max(0, Math.ceil(s*100)); }
function csToFmt(cs){
  cs = Math.max(0, Math.floor(cs));
  const h = Math.floor(cs/360000); cs%=360000;
  const m = Math.floor(cs/6000);   cs%=6000;
  const s = Math.floor(cs/100);    const c=cs%100;
  return h+':'+String(m).padStart(2,'0')+':'+String(s).padStart(2,'0')+'.'+String(c).padStart(2,'0');
}
function escASS(t){
  return String(t||'')
    .replace(/[\r\n]+/g, ' ')
    .replace(/\s+/g, ' ')
    .toUpperCase()
    .replace(/[{}]/g, m => m==='{' ? '\\{' : '\\}')
    .replace(/`/g, "'");
}
// Classifiers
function isLexical(word){ return /[A-Za-z]/.test(word); }
function isPunctOnly(word){ return /^[^A-Za-z0-9]+$/.test(word); }
const NUM_WITH_SUFFIX_RE = /^\d+(?:[.,]\d+)*(?:[a-z%]+)?$/i;

// Build one-line text with highlight token i
function createSubtitleText(group, hi){
  let line = '';
  for (let i=0;i<group.length;i++){
    if (i>0) line += ' ';
    const token = escASS(group[i].word).trim();
    if (i===hi){
      line += '{\\fs'+CONFIG.highlightFontSize+'\\c&H'+CONFIG.highlightColor+'&}'+token+'{\\r}';
    } else {
      line += token;
    }
  }
  return line;
}

function createGroups(words, maxPerGroup){
  const groups=[]; let cur=[];
  for (let i=0;i<words.length;i++){
    const w=words[i]; cur.push(w);
    const txt=String(w.word||'').trim();
    const endOfSentence=/[.!?]$/.test(txt);
    const atLimit=cur.length>=maxPerGroup;
    const last=i===words.length-1;
    if (atLimit||endOfSentence||last){ if (cur.length) groups.push(cur.slice()); cur=[]; }
  }
  if (cur.length) groups.push(cur);
  return groups;
}

// --- Numeric helpers (optional) ---
function mergeDecimalTriplets(ws){
  if (!CONFIG.combineNumbers) return ws;
  const out=[];
  for (let i=0;i<ws.length;i++){
    const a=ws[i], b=ws[i+1], c=ws[i+2];
    if (a&&b&&c && /^\d+$/.test(String(a.word)) && String(b.word)==='.' && /^\d+$/.test(String(c.word))){
      out.push({ word:String(a.word)+'.'+String(c.word), start:+a.start, end:+c.end });
      i+=2;
    } else out.push({ word:String(a.word), start:+a.start, end:+a.end });
  }
  return out;
}
function attachNumbersToPrevious(ws, maxGapSec){
  if (!CONFIG.combineNumbers || !ws.length) return ws;
  const out=[{...ws[0]}];
  for (let i=1;i<ws.length;i++){
    const cur=ws[i], prev=out[out.length-1];
    const gap = (+cur.start) - (+prev.end);
    const isNum = NUM_WITH_SUFFIX_RE.test(String(cur.word));
    if (isNum && gap>=0 && gap<=maxGapSec){
      prev.word = (String(prev.word||'').trim() + ' ' + String(cur.word||'').trim()).trim();
      prev.end = Math.max(+prev.end, +cur.end);
    } else out.push({...cur});
  }
  return out;
}

/**
 * Repair zero-duration and same-start clusters using next-start as ceiling.
 * Keeps lexical tokens, drops only tiny punctuation/empty tokens.
 */
function repairAndSanitize(ws){
  const tiny = +CONFIG.tinyDurThresholdSec || 0.03;
  const minLex = +CONFIG.minWordDurSec || 0.08;
  const kept = [];

  for (const w of ws){
    const word = String(w.word||'').trim();
    let s = +w.start || 0;
    let e = +w.end   || 0;

    if (CONFIG.dropEmpty && word.length===0) continue;

    if (isLexical(word)){
      if (!(e > s)) e = s + minLex; // synthesize
      kept.push({ word, start:s, end:e });
      continue;
    }
    // Non-lexical (numbers/punct/etc.)
    const dur = Math.max(0, e - s);
    if (CONFIG.dropPunctOnly && isPunctOnly(word) && dur < tiny) continue;
    if (!(e > s)) e = s + Math.max(minLex, tiny);
    kept.push({ word, start:s, end:e });
  }
  if (!kept.length) return kept;

  // Sort
  kept.sort((a,b)=> a.start - b.start || a.end - b.end);

  // Pass B: use next-start to give headroom to tiny/zero tokens
  for (let i=0;i<kept.length;i++){
    const cur = kept[i];
    const next = kept[i+1];
    if (!next) continue;
    const s = cur.start;
    let e = cur.end;
    const nextS = next.start;
    const dur = e - s;

    if (!(e > s) || dur < tiny){
      const EPS = 0.01; // 10ms
      const target = Math.max(minLex, tiny);
      const safeEnd = (nextS > s + EPS) ? Math.min(nextS - EPS, s + target) : s + target;
      if (safeEnd > e) cur.end = safeEnd;
    }
  }

  // Pass C: pack identical-start clusters into consecutive slices
  const clusterPacked = [];
  let i = 0;
  while (i < kept.length){
    const s = kept[i].start;
    const cluster = [kept[i]];
    let j = i+1;
    while (j < kept.length && Math.abs(kept[j].start - s) < 0.0005) { cluster.push(kept[j]); j++; } // ~0.5ms

    if (cluster.length === 1){
      clusterPacked.push(kept[i]); i=j; continue;
    }

    const nextDistinctStart = (j < kept.length) ? kept[j].start : (s + 0.25);
    const SLICE = Math.max(0.01, CONFIG.minWordDurSec || 0.08); // 10ms+
    let cursor = s;
    for (const tok of cluster){
      const end = Math.min(cursor + SLICE, nextDistinctStart - 0.005);
      clusterPacked.push({ word: tok.word, start: cursor, end: Math.max(end, cursor + 0.01) });
      cursor = Math.min(end, nextDistinctStart - 0.005);
    }
    i = j;
  }

  // Final monotonic guard
  for (let k=0;k<clusterPacked.length-1;k++){
    const a = clusterPacked[k], b = clusterPacked[k+1];
    if (b.start < a.end){
      b.start = a.end;
      if (!(b.end > b.start)) b.end = b.start + (CONFIG.minWordDurSec || 0.08);
    }
  }

  // Sort again
  clusterPacked.sort((a,b)=> a.start - b.start || a.end - b.end);
  return clusterPacked;
}

//////////////////// PARSE INPUT ////////////////////
// Accepts:
// - ElevenLabs: { words: [ {text,start,end,type,...}, ... ] }
// - Whisper:    { words: [ {word,start,end,...}, ... ] } or { segments: [...] }
const item = $input.first().json;
let response = (item && Array.isArray(item.data) && item.data[0]) ? item.data[0] : item;

let words = [];

// Case 1: direct words array
if (Array.isArray(response.words) && response.words.length){
  const first = response.words[0];

  // ElevenLabs format: has 'text' + 'type'
  if (first && typeof first.text === 'string' && 'type' in first){
    for (const w of response.words){
      // Keep only real words; ignore spacing/audio_event
      if (String(w.type).toLowerCase() !== 'word') continue;
      words.push({ word: String(w.text||''), start: Number(w.start||0), end: Number(w.end||0) });
    }
  } else {
    // Whisper verbose_json: has 'word'
    for (const w of response.words){
      words.push({ word: String(w.word||''), start: Number(w.start||0), end: Number(w.end||0) });
    }
  }
}
// Case 2: segments with nested words (Whisper or Scribe variations)
else if (Array.isArray(response.segments) && response.segments.length){
  for (const seg of response.segments){
    if (Array.isArray(seg.words) && seg.words.length){
      const firstW = seg.words[0];
      const isScribe = firstW && typeof firstW.text === 'string' && 'type' in firstW;
      for (const w of seg.words){
        if (isScribe){
          if (String(w.type).toLowerCase() !== 'word') continue;
          words.push({ word: String(w.text||''), start: Number(w.start||seg.start||0), end: Number(w.end||seg.end||0) });
        } else {
          words.push({ word: String(w.word||''), start: Number(w.start||seg.start||0), end: Number(w.end||seg.end||0) });
        }
      }
    } else {
      const ss = Number(seg.start||0);
      const ee = Number(seg.end ?? (ss + 0.6));
      words.push({ word: String(seg.text||'').trim(), start: ss, end: ee });
      CONFIG.wordsToShow = Math.max(CONFIG.wordsToShow, 999);
    }
  }
}

if (!words.length) throw new Error('No words found after parsing (did you pass Scribe/Whisper JSON?)');

// Sort & initial min-duration guard
words.sort((a,b)=>a.start-b.start || a.end-b.end);
for (const w of words){
  if (!(w.end > w.start)) w.end = w.start + Number(CONFIG.minWordDurSec||0.08);
}

// Optional numeric fixes (safe for Scribe/Whisper)
words = mergeDecimalTriplets(words);
words = attachNumbersToPrevious(words, CONFIG.attachNumMaxGapSec);

// Critical repair for zero-duration & same-start clusters
words = repairAndSanitize(words);

// Resolution fallback
CONFIG.videoWidth  = firstDefined(item.width,  item.videoWidth,  response.width,  1080);
CONFIG.videoHeight = firstDefined(item.height, item.videoHeight, response.height, 1920);

//////////////////// ASS HEADER ////////////////////
let ass = '';
ass += '[Script Info]\n';
ass += 'Title: Single-Token 10ms Quantized (Scribe/Whisper compatible)\n';
ass += 'ScriptType: v4.00+\n';
ass += 'PlayResX: ' + CONFIG.videoWidth + '\n';
ass += 'PlayResY: ' + CONFIG.videoHeight + '\n\n';
ass += '[V4+ Styles]\n';
ass += 'Format: Name, Fontname, Fontsize, PrimaryColour, SecondaryColour, OutlineColour, BackColour, Bold, Italic, Underline, StrikeOut, ScaleX, ScaleY, Spacing, Angle, BorderStyle, Outline, Shadow, Alignment, MarginL, MarginR, MarginV, Encoding\n';
ass += 'Style: Default,' + CONFIG.fontName + ',' + CONFIG.fontSize + ',&H' + CONFIG.fontColor + ',&H' + CONFIG.outlineColor + ',&H' + CONFIG.outlineColor + ',&H' + CONFIG.backgroundColor + ',' + CONFIG.bold + ',' + CONFIG.italic + ',' + CONFIG.underline + ',' + CONFIG.strikeout + ',' + CONFIG.scaleX + ',' + CONFIG.scaleY + ',' + CONFIG.spacing + ',' + CONFIG.angle + ',' + CONFIG.borderStyle + ',' + CONFIG.outline + ',' + CONFIG.shadow + ',' + CONFIG.alignment + ',' + CONFIG.marginL + ',' + CONFIG.marginR + ',' + CONFIG.marginV + ',1\n\n';
ass += '[Events]\n';
ass += 'Format: Layer, Start, End, Style, Name, MarginL, MarginR, MarginV, Effect, Text\n';

//////////////////// BUILD LINES ////////////////////
// groups (wordsToShow=1 => single token highlight stepping across words)
const groups = createGroups(words, CONFIG.wordsToShow);

const raw = [];
for (const g of groups){
  for (let i=0;i<g.length;i++){
    const w=g[i];
    raw.push({ text:createSubtitleText(g,i), start:w.start, end:w.end });
  }
}

const minDurCs = Math.max(1, Math.round(CONFIG.minWordDurSec * 100)); // ‚â•1 cs
const minGapCs = Math.max(0, Number(CONFIG.minGapCs||0));            // allow 0 if needed
const offsetMs = Number(CONFIG.globalOffsetMs||0);
const maxShift = Math.max(0, Number(CONFIG.maxStartShiftCs||0));

const lines = raw.map(L => ({
  text: L.text,
  s: toCsStart(L.start, offsetMs),
  e: toCsEnd(L.end, offsetMs),
}));

// Ensure minimum duration
for (const L of lines){
  if (L.e < L.s + minDurCs) L.e = L.s + minDurCs;
}

// Keep starts; if tight, gently push next start up to maxShift
for (let i=0;i<lines.length-1;i++){
  const a=lines[i], b=lines[i+1];
  let latestEnd = b.s - minGapCs;

  if (latestEnd < a.s + minDurCs){
    const need = (a.s + minDurCs + minGapCs) - b.s;
    const canShift = Math.min(maxShift, Math.max(0, need));
    if (canShift > 0){
      b.s += canShift;
      if (b.e < b.s + minDurCs) b.e = b.s + minDurCs;
      latestEnd = b.s - minGapCs;
    }
  }

  const minEnd = a.s + minDurCs;
  a.e = Math.min(a.e, latestEnd);
  if (a.e < minEnd) a.e = minEnd;

  if (b.s < a.e){
    b.s = a.e;
    if (b.e < b.s + minDurCs) b.e = b.s + minDurCs;
  }
}

// Final monotonic guards
for (let i=1;i<lines.length;i++){
  if (lines[i].s < lines[i-1].s) lines[i].s = lines[i-1].s;
  if (lines[i].e < lines[i].s + minDurCs) lines[i].e = lines[i].s + minDurCs;
}

// Emit
for (const L of lines){
  ass += 'Dialogue: 0,' +
    csToFmt(L.s) + ',' +
    csToFmt(L.e) + ',' +
    'Default,,0,0,0,,' +
    L.text + '\n';
}

//////////////////// RETURN ////////////////////
return [{
  json: {
    generated: true,
    width: CONFIG.videoWidth,
    height: CONFIG.videoHeight,
    words: words.length,
    lines: lines.length,
    globalOffsetMs: CONFIG.globalOffsetMs,
    maxStartShiftCs: CONFIG.maxStartShiftCs
  },
  binary: {
    data: {
      data: Buffer.from(ass,'utf8').toString('base64'),
      mimeType: 'text/ass',
      fileName: CONFIG.outputFileName,
    },
  },
}];

