// --- pull name safely ---
const apifyItems = $items("Apify POST4");
const apifyName =
  (apifyItems?.[0]?.json?.author?.name) ??
  (apifyItems?.[0]?.json?.author?.displayName) ??
  (apifyItems?.[0]?.json?.author?.userName);

const rawName = ($json.display_name ?? apifyName ?? "KREA AI");

// strip emoji/variation selectors/ZW chars to keep width calc stable
const name = String(rawName)
  .replace(/\p{Extended_Pictographic}/gu, "")      // emoji
  .replace(/[\u200B-\u200D\uFE0E\uFE0F]/g, "")     // ZWJ/ZWNJ/VS
  .trim();

// --- layout constants (your template) ---
const baseNameX = 160;
const fontSize  = 180;
const padding   = 22;

// --- calibration (from your KREA AI example) ---
const SCALE = 0.2006;

// --- character width model ---
const WIDE   = new Set([... "MW@%#&$0OQG"]);
const NARROW = new Set([... "il.:;!|`'1I"]);
const SPACE_EM = 0.30;
const AVG_EM   = 0.56;
const WIDE_EM  = 0.90;
const NARROW_EM= 0.35;

// sum em-widths
let emWidth = 0;
for (const ch of name) {
  if (ch === ' ') emWidth += SPACE_EM;
  else if (WIDE.has(ch)) emWidth += WIDE_EM;
  else if (NARROW.has(ch)) emWidth += NARROW_EM;
  else emWidth += AVG_EM;
}

// convert to template units
const rawPixels = emWidth * fontSize;
const textWidth = rawPixels * SCALE;

const badgeXRaw = baseNameX + textWidth + padding;

// clamp if you have a right bound
const MAX_RIGHT = 1500;
const badge_x = Math.min(Math.round(badgeXRaw), MAX_RIGHT);

return [{ json: { badge_x, nameUsed: name } }];
